package javacodes.dsa;

public class ReverseString {

	public static void main(String[] args) {
		System.out.println(solve(""));
	}
	
	private static String solve(String A) {
		
		String revString = "";
        for(int i=A.length()-1; i>=0; i--){
            revString = revString + A.charAt(i);
        }
        return revString;
	}

}
